<?php // No direct access	defined( '_JEXEC' ) or die( 'Restricted access' ); // Require the base controller	jimport('joomla.application.component.controller');		//class for controllerclass AdvancedordermanagerControllerreport extends JController{    		function display($tpl=null)		{			JRequest::setVar( 'view', 'report');			JRequest::setVar('layout', 'form');			$doc = &JFactory::getDocument();			$path='administrator/components/com_advancedordermanager/';			$doc->addStyleSheet(JURI::root().$path.'css'.'/'.'datep.css');			JToolBarHelper::title( JText::_( 'Advanced Order Manager' ), 'calculator' );					JToolBarHelper::back();						parent::display();						}					function showreport(){							JRequest::setVar('layout', 'default');				JToolBarHelper::title( JText::_( 'Advanced Order Manager' ), 'calculator' );				$path='administrator/components/com_advancedordermanager/';				$doc = &JFactory::getDocument();				$doc->addStyleSheet(JURI::root().$path.'css'.'/'.'datep.css');				JToolBarHelper::back();				$model = $this->getModel('report');							$view = & $this->getView( 'report', 'html' );				$view->setModel( $model, true );				$view->getreports();										}						function combochart()			{							//JRequest::setVar('layout', 'combo');				JToolBarHelper::title( JText::_( 'Advanced Order Manager' ), 'calculator' );				$path='administrator/components/com_advancedordermanager/';				$doc = &JFactory::getDocument();				$doc->addStyleSheet(JURI::root().$path.'css'.'/'.'datep.css');				JToolBarHelper::back();							$model = $this->getModel('report');							$view = & $this->getView( 'report', 'html' );				$view->setModel( $model, true );				$view->setLayout( 'combo');				$view->combochart();																					}				}					?>